wrong_prolist=[]
wrong_explist=[]
def print_pro(listt):
    def loop(listt,score1,do_num):
        if listt != []:
            lis_fir=listt[0]
            ind_num=해설리스트.index(lis_fir)
            print(do_num,"/10")
            print(lis_fir,"(이)라는 의미의 신조어는 무엇일까요??")
            ans=input("답:")
            if ans==문제리스트[ind_num]:
                print("맞았습니다!!! ㅊㅊ")
                score1=score1+10
                print("")
            else:
                print("틀렸어요..ㄲㅂ")
                time.sleep(1)
                print("정답은..",문제리스트[ind_num],"입니다.")
                wrong_prolist.append(문제리스트[ind_num])
                wrong_explist.append(해설리스트[ind_num])
                print("")
            listt.remove(lis_fir)
            time.sleep(2)
            return loop(listt,score1,do_num+1)
        else:
            print("문제 종료!!!!!!")
            time.sleep(2)
            print("당신의 점수는....?")
            countdown()
            print("당신의 점수는",score1,"/100점 입니다!!")
            print("")
        time.sleep(2)
        print("문제를 푸셨다면 복습을 하셔야 합니다..")
        rev=input("복습을 원하신다면 ㅇㅇ, 귀찮으면 ㄴㄴ을 누르세요!:")
        rev=askag(rev)
        if (rev=='ㅇㅇ'):
            print("")
            print("복습해보자~~!!")
            #print("너가 틀린 문제는",wrong_prolist.count(),"개 있어")
            while wrong_prolist != []:
                time.sleep(2)
                print(wrong_prolist[0],"의 뜻은",wrong_explist[0])
                wrong_prolist.remove(wrong_prolist[0])
                wrong_explist.remove(wrong_explist[0])
            time.sleep(2)
            print("복습 끝~~~~")
            return score1
        else:
            return score1
    return loop(listt,0,1)
