import random
def solvepro():
    level=input("난이도를 선택하세요.(상,중,하,랜덤):")
    print("")
    print("당신은",level,"난이도를 선택하셨습니다!")
    time.sleep(2)
    if level=='상':
        high=random.sample(상문제해설,10)
        print("자자 문제 나갑니다.")
        print("답을 모르거나 풀기 싫으면 아무키나 눌러주세요.")
        print("")
        time.sleep(2)
        score1 = print_pro(high)
    elif level=='중':
        mid=random.sample(중문제해설,10)
        print("자자 문제 나갑니다.")
        print("답을 모르거나 풀기 싫으면 아무키나 눌러주세요.")
        print("")
        time.sleep(2)
        score1 = print_pro(mid)
    elif level=='하':
        low=random.sample(하문제해설,10)
        print("자자 문제 나갑니다.")
        print("답을 모르거나 풀기 싫으면 아무키나 눌러주세요.")
        print("")
        time.sleep(2)
        score1 = print_pro(low)
    elif level=='랜덤':
        rando=random.sample(해설리스트,10)
        print("자자 문제 나갑니다.")
        print("답을 모르거나 풀기 싫으면 아무키나 눌러주세요.")
        print("")
        time.sleep(2)
        score1 = print_pro(rando)
    else:
        print(level,"난이도는 없습니다… 상,중,하,랜덤만 입력해주세요.")
        return solvepro()
    return level, score1


